/**
 * Add a new file to an existing data item.  Don't trust the code generated by the widget, there are examples of using this API in Python, cURL and Node.js under the parameters.
 * Method: POST
 * URL: https://studio.edgeimpulse.com/api/organizations/:organizationId/data/:dataId/add
 */
export async function add_files(params: any, apiKey: string) {
  // TODO: Implement parameter mapping
  const res = await fetch(`https://studio.edgeimpulse.com/api/organizations/:organizationId/data/:dataId/add`, {
    method: 'POST',
    headers: {
      'x-api-key': apiKey,
      'Content-Type': 'application/json',
      'Accept': 'application/json',
    },
    // body: JSON.stringify(params), // Uncomment for POST/PUT
  });
  return res.json();
}
